INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (446, 'Experience Congress LATAM - 333', '20240829Captura-de-Pantalla-2024-08-29-a-las-12.14.05.png', '2024-10-30', '2024-10-31', '9:00', '16:00', '2', 'San Jose - Costa Rica', 'Este congreso busca fortalecer las alianzas tanto a nivel centroamericano como latinoamericano, fomentando conexiones estratégicas y forjando la unión y confianza entre todos los actores del sector.', 1, '2', '1', 'https://www.333experiencecongresslatam.com/', 'experience-congress-latam-333', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (447, 'Avances en Nutrición y Alimentación Animal - FEDNA', '20240829Captura-de-Pantalla-2024-08-29-a-las-12.21.40.png', '2024-11-06', '2024-11-07', '9:00', '18:00', '3', 'Madrid - España', 'La principal actividad de FEDNA consiste en la organización de un congreso anual, en el cual se exponen diversos aspectos relacionados con los últimos avances en Nutrición y Alimentación Animal. Este congreso reúne a un gran número de representantes del sector de la alimentación animal, tanto de carácter nacional como extranjeros (500-600 asistentes en las últimas ediciones). Dicho congreso se celebra en Madrid, habitualmente en Noviembre. El número de ponencias y mesas redondas está en torno a 14 y son impartidas o moderadas por líderes de la industria de fabricación de piensos, y por investigadores en nutrición animal. La presentación de trabajos corresponde en un 30% a investigadores nacionales, un 30% de técnicos extranjeros y el restante 40% a nutricionistas nacionales. La finalidad es exponer al sector de piensos lo que hay de nuevo en nutrición y cómo aplicar estos nuevos conocimientos a nivel práctico.\r\n\r\n', 1, '4', '1', 'https://www.fundacionfedna.org/node/73', 'avances-en-nutricion-y-alimentacion-animal-fedna', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
