INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (472, 'Sommet de l\'élevage', '20240927logo-sde-horizontal-dark-bg-no-p-rgb.png', '01-10-2024', '04-10-2024', '10:45', '10:45', '4', 'Clermont-Ferrand - France', 'The Livestock Summit, the World Summit for Sustainable Livestock, is the largest European gathering dedicated to the livestock and agriculture industries.\r\n\r\nOur annual event brings together professionals from around the world to exchange innovative ideas, showcase exceptional animals, discover the latest innovations, and promote best practices in livestock farming.\r\n\r\nExplore our exhibitions, attend cutting-edge conferences, and participate in renowned cattle, sheep, and equine competitions. The Livestock Summit, where excellence meets innovation in the livestock sector.', 1, '4', 'Select type of event', 'https://www.sommet-elevage.fr/en/', 0, 1, 'sommet-de-lelevage', 'Sommet de l\'élevage', 'Sommet de l\'élevage', 0, 0, '', '', 'https://www.sommet-elevage.fr/en/', 0, 0, NULL, NULL, '', 'evento-472', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (473, 'Sepor', '20240927logosepor.png', '2024-10-28', '2024-10-31', '9:00', '18:00', '5', 'Murcia - España', 'Este año celebramos nuestra feria número 57 con el objetivo de superar los datos récord de asistencia de empresas, profesionales y público de la anterior edición. Fuimos testigo de un sector que sigue siendo un pilar fundamental de la economía de nuestro país y de un certámen que cada año es más grande e imprescindible.\r\nSEPOR es el lugar de encuentro de una importantísima comunidad, y este año volverá a serlo. Este año dejaremos claro que este sector es parte de un patrimonio muy valioso, con una cultura de innovación, modo de vida y apertura al mundo con unas características etnográficas propias. Queremos reivindicar el valor de la cultura de nuestro sector, que no es más que una forma de vida en pro de la sociedad.\r\nEn su 57º edición, SEPOR presenta importantes novedades orientadas a seguir mejorando la calidad y cantidad de servicios que se ofrecen a patrocinadores, colaboradores, expositores y asistentes. ¡Contamos contigo!', 1, '4', '2', 'https://www.seporlorca.com/', 'sepor', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (474, 'Expo AgriTech - La Feria del Campo', '20241004expoagritech-logo-ligh-e1724929706316.png', '2024-11-26', '2024-11-28', '12:15', '12:15', '6', 'Málaga - Spain', 'Expo AgriTech es el único evento tecnológico, industrial y de innovación para el sector del campo que reúne a los líderes del sector con el objetivo de ayudar a las explotaciones agrarias a mejorar su competitividad y productividad.\r\n\r\nExpo AgriTech es un espacio único de innovación y tecnología agroalimentaria para las Pymes Españolas del sector en el que adquirir los conocimientos y soluciones por las que la tecnología alimentaria está dirigiendo el crecimiento económico y la competitividad.', 1, '4', '2', 'https://www.expoagritech.com/', 'expo-agritech-la-feria-del-campo', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
INSERT INTO events (id, name, image, start_date, end_date, start_time, end_time, color, location, description, state, region, tipo, website, slugevento, status, idioma, user_id, created_at, updated_at) VALUES (475, 'PorciForum Mexico', '20241030Logo-PorciForum-MX24-(1).png', '2024-11-06', '2024-11-07', '13:00', '13:00', '2', 'Guadalajara - Mexico', 'La 1a Edición del porciFORUM México tendrá lugar en el Radisson Hotel Tapatío Guadalajara, un emblemático hotel localizado en San Pedro de Tlaquepaque, muy cerca del Aeropuesto de Guadalajara. Además, es un hotel muy bien localizado próximo a la gran parte de la producción porcina méxicana.', 1, '2', '1', 'https://porciforum.info/', 'porciforum-mexico', 'PUBLISHED', 1, 1, NOW(), NOW()) ON CONFLICT (slugevento) DO NOTHING;
